import { useState, useEffect } from "react";
import axios from "axios";
import Card from '../Card/Card';
import { Link } from "react-router-dom";

const CategoryComponent = ({ limit, category }) => {
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(true); // Изначально загрузка включена

  useEffect(() => {
    setLoading(true); // Включаем загрузку перед запросом
    setData([]); // Очищаем старые данные, если меняется категория
    axios(
      limit
        ? `https://fakestoreapi.com/products/category/${category}?limit=${limit}`
        : `https://fakestoreapi.com/products/category/${category}`
    )
      .then(({ data }) => {
        setData(data); // Устанавливаем данные в состояние
        setLoading(false); // Отключаем загрузку после получения данных
      })
      .catch(() => {
        setLoading(false); // Если произошла ошибка, все равно отключаем загрузку
      });
  }, [category, limit]);

  return (
    <div className="container">
      <h1><Link to={`/category/${category}`}>{category}</Link></h1>
      {loading ? (
        <div className="loader">.</div> // Показываем прелоадер
      ) : (
        <div className="row">
          {data.map(item => {
            return <Card item={item} key={item.id} />;
          })}
        </div>
      )}
    </div>
  );
};

export default CategoryComponent;
